mapper.select:
		select 
		sb.studentNo,
		sb.studentName,
		sb.sex,sb.college,
		sb.major,
		sb.className,
		sa.advanceActivity,
		sa.advTime,
		sa.advLevel,
		sa.examState,
		sa.remarks 
		from stu_basic_info sb inner join stu_adv_info sa on 
	   	sb.studentNo = sa.studentNo where 1 = 1

	  		and sb.studentNo = #{studentNo}


	  		and sb.studentName = #{studentName}


	  		and sb.college = #{college}


	  		and sb.major = #{major}


	  		and sb.className = #{className}


	  		and sb.advanceActivity = #{advanceActivity}


	  		and sb.advLevel = #{advLevel}


	  		and sb.advTime = #{advTime}


        	and sa.examState=#{examState}


	  		and sb.remarks = #{remarks}

        limit #{start},#{number};

		select 
		sb.studentNo,
		sb.studentName,
		sb.sex,
		sb.college,
		sb.major,
		sb.className,
		sa.advanceActivity,
		sa.advTime,
		sa.advLevel,
		sa.examState,
		sa.remarks 
		from stu_basic_info sb inner join stu_adv_info sa on 
	   	sb.studentNo = sa.studentNo where 1 = 1

	  		and sb.studentNo = #{studentNo}


	  		and sb.studentName = #{studentName}


	  		and sb.college = #{college}


	  		and sb.major = #{major}


	  		and sb.className = #{className}


	  		and sb.advanceActivity = #{advanceActivity}


	  		and sb.advLevel = #{advLevel}


	  		and sb.advTime = #{advTime}


        	and sa.examState=#{examState}


	  		and sb.remarks = #{remarks}

        	;

		select 
		count(*)
		from stu_basic_info sb inner join stu_adv_info sa on 
	   	sb.studentNo = sa.studentNo where 1 = 1

	  		sb.studentNo = #{studentNo}


	  		and sb.studentName = #{studentName}


	  		and sb.college = #{college}


	  		and sb.major = #{major}


	  		and sb.className = #{className}


	  		and sb.advanceActivity = #{advanceActivity}


	  		and sb.advLevel = #{advLevel}


	  		and sb.advTime = #{advTime}


        	and sa.examState=#{examState}


	  		and sb.remarks = #{remarks}

        	;
